const fetch = require('node-fetch');

try {
    // Extract the variable from the LLM output
    const data = $data || '';  // The data is a long string, pipe-delimited rows and columns

    // Create the data object
    const requestData = {
        data: data  // Include the pipe-delimited data
    };

    // Set up the URL and options for the fetch call
    const url = 'https://hook.us1.make.com/tteu9366w8j76cg987b9nzbcqbkl4nuw';
    const options = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestData)
    };

    const response = await fetch(url, options);
    const text = await response.text();
    return text;
} catch (error) {
    console.error('Error:', error);
    return '';
}
